import React from "react";
import styled from "styled-components";
import { useState } from "react";

export default function Navbar() {
  const [state, setstate] = useState("auto");

  const [Imgstate, setImgstate] = useState("/navbarimgcontainer/menu.png");

  const [inner_nav_state, setinner_nav_state] = useState("none");

  const [Inner_option_show1, setInner_option_show1] = useState("none");
  const [Inner_option_show2, setInner_option_show2] = useState("none");
  const [Inner_option_show3, setInner_option_show3] = useState("none");

  const [DropDownImg1, setDropDownImg1] = useState(
    "/navbarimgcontainer/droup_down.png"
  );
  const [DropDownImg2, setDropDownImg2] = useState(
    "/navbarimgcontainer/droup_down.png"
  );
  const [DropDownImg3, setDropDownImg3] = useState(
    "/navbarimgcontainer/droup_down.png"
  );
  const [Count1, setCount1] = useState(1);
  const [Count2, setCount2] = useState(1);
  const [Count3, setCount3] = useState(1);

  const [Switch, setSwitch] = useState("auto");

  const ShowNavBarContent = () => {
    if (Imgstate === "/navbarimgcontainer/menu.png") {
      setImgstate("/navbarimgcontainer/close.png");
      setstate("230px");
      setinner_nav_state("flex");
    } else {
      setstate("auto");
      setinner_nav_state("none");
    }
  };

  const ShowNavInnerContent1 = () => {

    setCount1(Count1 + 1);
    setCount2(1);
    setCount3(1);
    console.log("count1 : " + Count1);
    if (Count1 % 2 != 0) {

      // setInner_option_show1("flex");
      setInner_option_show2("none");
      setInner_option_show3("none");
      setCount1(Count1 + 1);
      setDropDownImg1("/navbarimgcontainer/arrow-up.png");
      setDropDownImg2("/navbarimgcontainer/droup_down.png");
      setDropDownImg3("/navbarimgcontainer/droup_down.png");
    } else {
      setDropDownImg1("/navbarimgcontainer/droup_down.png");
      setInner_option_show1("none");
      setInner_option_show2("none");
      setInner_option_show3("none");
    }
  };
  const ShowNavInnerContent2 = () => {
    setCount2(Count2 + 1);
    setCount3(1);
    setCount1(1);
    if (Count2 % 2 != 0) {
      setInner_option_show1("none");
      setInner_option_show2("flex");
      setInner_option_show3("none");
      setCount2(Count2 + 1);
      setDropDownImg2("/navbarimgcontainer/arrow-up.png");
      setDropDownImg1("/navbarimgcontainer/droup_down.png");
      setDropDownImg3("/navbarimgcontainer/droup_down.png");
    } else {
      setDropDownImg2("/navbarimgcontainer/droup_down.png");
      setInner_option_show1("none");
      setInner_option_show2("none");
      setInner_option_show3("none");
    }
  };
  const ShowNavInnerContent3 = () => {
    setCount1(1);
    setCount2(1);
    setCount3(Count3 + 1);
    if (Count3 % 2 != 0) {
      setInner_option_show1("none");
      setInner_option_show2("none");
      setInner_option_show3("flex");
      setCount3(Count3 + 1);
      setDropDownImg3("/navbarimgcontainer/arrow-up.png");
    } else {
      setDropDownImg3("/navbarimgcontainer/droup_down.png");
      setDropDownImg1("/navbarimgcontainer/droup_down.png");
      setDropDownImg2("/navbarimgcontainer/droup_down.png");
      setInner_option_show1("none");
      setInner_option_show2("none");
      setInner_option_show3("none");
    }
  };
  return (
    <MainContainer>
      <NavbarMainContainer
        className="NavbarContainerForSwitching"
        style={{ height: state }}
      >
        <NavbarContainerForSwitch>
          <LogoImg>
            <img
              src="/navbarimgcontainer/website_logo.png"
              alt="HouseHoldWebsite"
            />
          </LogoImg>

          {/* below navbar items will appear to the screen until the media query we have is not fired   */}

          <NavbarContent className="NavbarItemsForLaptopScreen">
            <ul className="Navbar_ul">
              <div className="NavContentContainer">
                <div className="NavContent">
                  <a href="/Home">
                    <li className="Navbar_li">Home</li>
                  </a>
                </div>
              </div>

              <div className="NavContentContainer">
                <div className="NavContent">
                  <a href="/Maintenance">
                    <li className="Navbar_li">Maintainance</li>
                  </a>
                  <div className="InnerOptions">
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Electrical supplies</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Water Supplies</li>
                    </a>
                  </div>
                </div>
              </div>

              <div className="NavContentContainer">
                <div className="NavContent">
                  <a href="/HouseHoldThings">
                    <li className="Navbar_li">HouseHold Things</li>
                  </a>
                  <div className="InnerOptions">
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Kid accessories</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Cleaning tools</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Bathroom accessories</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Toilet accessories</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">
                        Storage and organinzing
                      </li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Kitchen accessories</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Home decore</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">
                        Feature and categories
                      </li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Beauty and health</li>
                    </a>
                  </div>
                </div>
              </div>

              <div className="NavContentContainer">
                <div className="NavContent">
                  <a href="/MedicalFacilities">
                    <li className="Navbar_li">Medical Facilities</li>
                  </a>
                  <div className="InnerOptions">
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Medicines</li>
                    </a>
                    <a href="#" className="InnerOptions_a">
                      <li className="InnerOptions_li">Check Up</li>
                    </a>
                  </div>
                </div>
              </div>

              <div className="NavContentContainer">
                <div className="NavContent">
                  <a href="/About">
                    <li className="Navbar_li">About</li>
                  </a>
                </div>
              </div>
            </ul>
          </NavbarContent>

          {/* below navbar items content will appear to us only till media query is accessed */}

          <NavBarForPhoneWindow className="NavForPhoneWindow">
            <div className="NavBarForPhoneWindowMainContainer">
              <img
                src={Imgstate}
                alt="HouseHoldWebsite"
                onClick={ShowNavBarContent}
              />
            </div>
          </NavBarForPhoneWindow>
        </NavbarContainerForSwitch>

        <NavbarInnerContentContainer className="navbar_inner_content_container">
          <ul className="Phone_nav_ul" style={{ display: inner_nav_state }}>
            <div className="Phone_nav_details">
              <a href="" className="Phone_nav_a">
                <li className="Phone_nav_li">Home</li>
              </a>
            </div>

            <div className="Phone_nav_details" style={{height:Switch}}>
              <div className="Phone_Nav">
                <a href="" className="Phone_nav_a">
                  <li className="Phone_nav_li">Maintainance</li>
                </a>
                <div className="DropDownImg">
                  <img
                    src={DropDownImg1}
                    alt="HouseHoldWebsite"
                    onClick={ShowNavInnerContent1}
                  />
                </div>
              </div>

              <div
                className="Phone_nav_innerContent"
                id="Phone_nav_innerContent1"
                style={{ display: Inner_option_show1 }}
              >
                <a href="" className="Phone_nav_inner_a">
                  <li>Electrical supplies</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Water supplies</li>
                </a>
              </div>
            </div>

            <div className="Phone_nav_details">
              <a href="" className="Phone_nav_a">
                <li className="Phone_nav_li">HouseHold Things</li>
              </a>
              <div className="DropDownImg">
                <img
                  src={DropDownImg2}
                  alt="HouseHoldWebsite"
                  onClick={ShowNavInnerContent2}
                />
              </div>
              <div
                className="Phone_nav_innerContent"
                id="Phone_nav_innerContent2"
                style={{ display: Inner_option_show2 }}
              >
                <a href="" className="Phone_nav_inner_a">
                  <li>Kid accessories</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Cleaning tools</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Bathroom accessories</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Toilet accessories</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Storage and organizing</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Kitchen accessories</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Home Decore</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Feature and categories</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Beauty and health</li>
                </a>
              </div>
            </div>

            <div className="Phone_nav_details">
              <a href="" className="Phone_nav_a">
                <li className="Phone_nav_li">Medical Facilities</li>
              </a>
              <div className="DropDownImg">
                <img
                  src={DropDownImg3}
                  alt="HouseHoldWebsite"
                  onClick={ShowNavInnerContent3}
                />
              </div>
              <div
                className="Phone_nav_innerContent"
                id="Phone_nav_innerContent3"
                style={{ display: Inner_option_show3 }}
              >
                <a href="" className="Phone_nav_inner_a">
                  <li>Medicines</li>
                </a>
                <a href="" className="Phone_nav_inner_a">
                  <li>Check Up</li>
                </a>
              </div>
            </div>

            <div className="Phone_nav_details">
              <a href="" className="Phone_nav_a">
                <li className="Phone_nav_li">About</li>
              </a>
            </div>
          </ul>
        </NavbarInnerContentContainer>
      </NavbarMainContainer>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 100%;
  box-shadow: 0 0 10px 3px #472d2d;
