      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      padding: 3px;
      margin: 5px;
      width: 100%;
      box-sizing: border-box;

      .NavContent {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        box-sizing: border-box;
        width: 100%;
        border-radius: 4px;

        a {
          text-decoration: none;
          color: #472d2d;
          font-size: 1.2rem;
          box-sizing: border-box;
          width: 100%;
          letter-spacing: 0.3px;

          .Navbar_li {
            font-family: "Alkatra", cursive;
            cursor: pointer;
            border-radius: 4px;
            text-align: center;
            background-color: white;
            width: 100%;
            box-sizing: border-box;
          }
        }

        /* css for inner option */

        .InnerOptions {
          position: absolute;
          top: 69%;
          width: 17%;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          justify-content: center;
          color: #472d2d;
          box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px,
            rgba(0, 0, 0, 0.1) 0px 8px 16px;
          border-radius: 5px;
          background-color: white;
          display: none;
          z-index: 3;

          .InnerOptions_a {
            width: 100%;
            box-sizing: border-box;

            .InnerOptions_li {
              width: 100%;
              box-sizing: border-box;
              font-family: "Alkatra", cursive;
              font-size: 1.1rem;
              padding: 4px;

              &:hover {
                background-color: #553939;
                border-radius: 3px;
                color: white;
              }
            }
          }
        }
      }

      .NavContent:hover {
        .InnerOptions {
          display: flex;
        }
        .Navbar_li {
          background-color: #a77979;
          color: white;
        }
      }
    }
  }
`;

