import React from "react";
import styled from "styled-components";

export default function Navbar() {
  return (
    <MainContainer>
      <NavbarMainContainer>
        <LogoImg>
          <img
            src="/navbarimgcontainer/website_logo.png"
            alt="HouseHoldWebsite"
          />
        </LogoImg>
        <NavbarContent>
          <ul className="Navbar_ul">
            <div className="NavContentContainer">
              <div className="NavContent">
                <a href="/Home">
                  <li className="Navbar_li">Home</li>
                </a>
              </div>
            </div>

            <div className="NavContentContainer">
              <div className="NavContent">
                <a href="/Maintenance">
                  <li className="Navbar_li">Maintainance</li>
                </a>
                <div className="InnerOptions">
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Electrical supplies</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Water Supplies</li>
                  </a>
                </div>
              </div>
            </div>

            <div className="NavContentContainer">
              <div className="NavContent">
                <a href="/HouseHoldThings">
                  <li className="Navbar_li">HouseHold Things</li>
                </a>
                <div className="InnerOptions">
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Kid accessories</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Cleaning tools</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Bathroom accessories</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Toilet Accessories</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Storage And Organinzing</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Kitchen Accessories</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Home Decore</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Feature and categries</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Beauty and health</li>
                  </a>
                </div>
              </div>
            </div>

            <div className="NavContentContainer">
              <div className="NavContent">
                <a href="/MedicalFacilities">
                  <li className="Navbar_li">Medical Facilities</li>
                </a>
                <div className="InnerOptions">
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Medicines</li>
                  </a>
                  <a href="#" className="InnerOptions_a">
                    <li className="InnerOptions_li">Check Up</li>
                  </a>
                </div>
              </div>
            </div>

            <div className="NavContentContainer">
              <div className="NavContent">
                <a href="/About">
                  <li className="Navbar_li">About</li>
                </a>
              </div>
            </div>
          </ul>
        </NavbarContent>
      </NavbarMainContainer>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 100%;
  box-shadow: 0 0 10px 3px #472d2d;
  background-color: #472d2d;
  position: fixed;
  top: 0%;
  left: 0%;
  z-index: 3;
`;

const NavbarMainContainer = styled.nav`
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

const LogoImg = styled.div`
  width: 5%;
  padding: 4px;
  margin: 4px;

  img {
    width: 100%;
    filter: invert(100%);
  }
`;

const NavbarContent = styled.div`
  width: 100%;

  .Navbar_ul {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-evenly;
    list-style: none;

    .NavContentContainer {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      padding: 3px;
      margin: 5px;
      width: 100%;
      box-sizing: border-box;

      .NavContent {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        box-sizing: border-box;
        width: 100%;
        border-radius: 4px;

        a {
          text-decoration: none;
          color: #472d2d;
          font-size: 1.2rem;
          box-sizing: border-box;
          width: 100%;
          letter-spacing: 0.3px;

          .Navbar_li {
            font-family: "Alkatra", cursive;
            cursor: pointer;
            border-radius: 4px;
            text-align: center;
            background-color: white;
            width: 100%;
            box-sizing: border-box;
          }
        }

        /* css for inner option */

        .InnerOptions {
          position: absolute;
          top: 72%;
          width: 17%;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          justify-content: center;
          color: #472d2d;
          /* box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px,
            rgba(0, 0, 0, 0.1) 0px 8px 16px ; */
          border-radius: 5px;
          /* box-shadow: 0 0 10px 3px #472d2d; */
          background-color: white;
          display: none;
          z-index: 3;

          .InnerOptions_a {
            width: 100%;
            box-sizing: border-box;

            .InnerOptions_li {
              width: 100%;
              box-sizing: border-box;
              font-family: "Alkatra", cursive;
              font-size: 1.1rem;
              padding: 4px;
           
              :hover {
                background-color: #553939;
                border-radius: 3px;
                color: white;
              }
            }
          }
        }
      }

      .NavContent:hover {
        .InnerOptions {
          display: flex;
        }
        .Navbar_li {
          background-color: #A77979;
          color: white;
        }
      }
    }
  }
`;
