import React from "react";
import styled from "styled-components";

export default function Navbar() {
  return (
    <MainContainer>
      <NavbarMainContainer>
        <div className="NavbarContainerForSwitching">


        </div>
        
      </NavbarMainContainer>
    </MainContainer>
  );
}

const MainContainer = styled.div`
  width: 100%;
  box-shadow: 0 0 10px 3px #472d2d;
  background-color: #472d2d;
  position: fixed;
  top: 0%;
  left: 0%;
  z-index: 3;

  @media (min-width: 0px) and (max-width: 936px) {
    .NavbarItemsForLaptopScreen {
      display: none;
    }
  }
  @media (min-width: 937px) {
    .NavForPhoneWindow {
      display: none;
    }
  }
`;

const NavbarMainContainer = styled.nav`
  width: 100%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

const LogoImg = styled.div`
  width: 6%;
  padding: 4px;
  margin: 4px;
  img {
    width: 100%;
    filter: invert(100%);
  }
`;

const NavbarContent = styled.div`
  width: 100%;
  .Navbar_ul {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: space-evenly;
    list-style: none;

    .NavContentContainer {
      display: flex;
      flex-direction: column;
      align-items: center;
      justify-content: center;
      padding: 3px;
      margin: 5px;
      width: 100%;
      box-sizing: border-box;

      .NavContent {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        box-sizing: border-box;
        width: 100%;
        border-radius: 4px;

        a {
          text-decoration: none;
          color: #472d2d;
          font-size: 1.2rem;
          box-sizing: border-box;
          width: 100%;
          letter-spacing: 0.3px;

          .Navbar_li {
            font-family: "Alkatra", cursive;
            cursor: pointer;
            border-radius: 4px;
            text-align: center;
            background-color: white;
            width: 100%;
            box-sizing: border-box;
          }
        }

        /* css for inner option */

        .InnerOptions {
          position: absolute;
          top: 72%;
          width: 17%;
          display: flex;
          flex-direction: column;
          align-items: flex-start;
          justify-content: center;
          color: #472d2d;
          box-shadow: rgba(0, 0, 0, 0.1) 0px 2px 4px,
            rgba(0, 0, 0, 0.1) 0px 8px 16px;
          border-radius: 5px;
          background-color: white;
          display: none;
          z-index: 3;

          .InnerOptions_a {
            width: 100%;
            box-sizing: border-box;

            .InnerOptions_li {
              width: 100%;
              box-sizing: border-box;
              font-family: "Alkatra", cursive;
              font-size: 1.1rem;
              padding: 4px;

              &:hover {
                background-color: #553939;
                border-radius: 3px;
                color: white;
              }
            }
          }
        }
      }

      .NavContent:hover {
        .InnerOptions {
          display: flex;
        }
        .Navbar_li {
          background-color: #a77979;
          color: white;
        }
      }
    }
  }
`;

const NavBarForPhoneWindow = styled.div`
  width: 6%;
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  justify-content: center;
  margin-right: 2px;
  border: 2px solid yellow;
  .NavBarForPhoneWindowMainContainer {
    width: 100%;
    border: 2px solid blue;
    img {
      width: 100%;
      filter: invert(100%);
    }
  }

  .Navabar_Items_Container {
    border: 2px solid purple;
  }
`;

const NavBar_Items_Container = styled.div`
  border: 2px solid yellow;
`;
