                  <a href="/About">
                    <li className="Navbar_li">About</li>
                  </a>
                </div>
              </div>
            </ul>
          </NavbarContent>

          {/* below navbar items content will appear to us only till media query is accessed */}

          <NavBarForPhoneWindow className="NavForPhoneWindow">
            <div className="NavBarForPhoneWindowMainContainer">
              <img
                src={Imgstate}
                alt="HouseHoldWebsite"
                onClick={ShowNavBarContent}
              />
            </div>
          </NavBarForPhoneWindow>
        </NavbarContainerForSwitch>

        <NavbarInnerContentContainer className="navbar_inner_content_container">
          <div className="Phone_navbar">
            {/* first phone nav item  */}
            <div className="dropdown Drop_down_container">
              <button
                className="btn dropdown-toggle Drop_Down_button_container"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                onClick={() => showContent1()}
              >
                Home
              </button>
              {/* <ul className="dropdown-menu ">
                <li>
                  <a className="dropdown-item" href="#">
                    Action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Another action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Something else here
                  </a>
                </li>
              </ul> */}
            </div>

            {/* second phone nav item  */}

            <div className="dropdown Drop_down_container">
              <button
                className="btn dropdown-toggle Drop_Down_button_container"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                onClick={showContent2}
              >
                Maintainance
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#">
                    electrical supplies
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    water supplies
                  </a>
                </li>
              </ul>
            </div>

            {/* Third Phone nav item  */}
            <div className="dropdown Drop_down_container">
              <button
                className="btn  dropdown-toggle  Drop_Down_button_container"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                onClick={showContent3}
              >
                HouseHold Things
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#">
                  Kid accessories
                      
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                  Cleaning tools
                      Bathroom accessories
                      Toilet accessories
                      Storage and organinzing
                      Kitchen accessories
                      Home decore
                      Feature and categories
                      Beauty and health
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Something else here
                  </a>
                </li>
              </ul>
            </div>

            {/* phone nav fouth item  */}
            <div className="dropdown Drop_down_container">
              <button
                className="btn  dropdown-toggle Drop_Down_button_container"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                onClick={showContent4}
              >
                Dropdown button
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#">
                    Action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Another action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Something else here
                  </a>
                </li>
              </ul>
            </div>

            {/* phone nav fifth item  */}
            <div className="dropdown Drop_down_container">
              <button
                className="btn dropdown-toggle Drop_Down_button_container"
                type="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
                onClick={showContent5}
              >
                Dropdown button
              </button>
              <ul className="dropdown-menu">
                <li>
                  <a className="dropdown-item" href="#">
                    Action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Another action
                  </a>
                </li>
                <li>
                  <a className="dropdown-item" href="#">
                    Something else here
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </NavbarInnerContentContainer>
      </NavbarMainContainer>
    </MainContainer>
  );
}

